/*
  Premium Invoice Template - Professional Design
  AANGEPAST voor "Run Once for Each Item" mode
*/

const DEFAULT_VAT = 0.21;
function num(n, dec = 2) { 
  return Number(n || 0).toFixed(dec); 
}
function formatCurrency(amount) {
  return new Intl.NumberFormat('nl-NL', { 
    style: 'currency', 
    currency: 'EUR' 
  }).format(amount);
}

const orderData = $json;
console.log(`Processing invoice for order:`, orderData.orderId);

const date = new Date().toLocaleDateString('nl-NL', {
  year: 'numeric',
  month: 'long', 
  day: 'numeric'
});

const klant = orderData.billingDetails ?? {};
const naam = `${klant.firstName ?? ''} ${klant.surname ?? ''}`.trim() || 'Gewaardeerde klant';
const adres = `${klant.streetName ?? ''} ${klant.houseNumber ?? ''} ${klant.houseNumberExtension ?? ''}`.trim();
const postcodeCity = `${klant.zipCode ?? ''} ${klant.city ?? ''}`.trim();
const email = klant.email ?? 'Niet opgegeven';
const orderRef = orderData.orderReference ?? orderData.orderId ?? '‚Äì';
const factuurnummer = `F-${Date.now().toString().slice(-6)}`;

// Correcte verwijzing naar orderItems
const orderLines = orderData.orderItems?.orderItems ?? orderData.orderItems ?? [];

if (orderLines.length === 0) {
  throw new Error('Geen order items gevonden in de order data');
}

let rows = '', exTotal = 0, vatTotal = 0, grandTotal = 0;

for (const d of orderLines) {
  const qty = Number(d.quantity ?? 1);
  const vatRate = Number(d.vatRate ?? DEFAULT_VAT);
  const priceInc = Number(d.unitPrice ?? d.priceInc ?? d.price ?? 0);

  if (priceInc <= 0) {
    console.warn(`Geen geldige prijs gevonden voor product ${d.ean}`);
    continue;
  }

  const priceEx = priceInc / (1 + vatRate);
  const lineEx = priceEx * qty;
  const lineVat = (priceInc - priceEx) * qty;
  const lineTotal = priceInc * qty;

  exTotal += lineEx;
  vatTotal += lineVat;
  grandTotal += lineTotal;

  rows += `
    <tr>
      <td>${d.ean || '‚Äì'}</td>
      <td>${d.title || d.product?.title || 'Product'}</td>
      <td class="center">${qty}</td>
      <td class="right">${formatCurrency(priceEx)}</td>
      <td class="center">${num(vatRate * 100, 0)}%</td>
      <td class="right">${formatCurrency(lineTotal)}</td>
    </tr>`;
}

const html = `
<!DOCTYPE html>
<html lang="nl">
<head>
  <meta charset="utf-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Factuur ${factuurnummer}</title>
  <style>
    @page {
      size: A4;
      margin: 20mm;
    }

    body {
      font-family: Arial, sans-serif;
      color: #333;
      line-height: 1.4;
      margin: 0;
      padding: 0;
      font-size: 12pt;
    }

    .header {
      display: flex;
      justify-content: space-between;
      align-items: flex-start;
      margin-bottom: 30px;
      padding-bottom: 15px;
      border-bottom: 1px solid #ddd;
    }

    .header h1 {
      font-size: 24pt;
      font-weight: bold;
      color: #333;
      margin: 0;
    }

    .invoice-details {
      text-align: right;
      font-size: 11pt;
    }

    .invoice-details div {
      margin-bottom: 5px;
    }

    .customer-info {
      margin-bottom: 25px;
    }

    .customer-info h3 {
      margin: 0 0 10px 0;
      font-size: 14pt;
      font-weight: bold;
    }

    .customer-details {
      font-size: 11pt;
      line-height: 1.5;
    }

    .items-table {
      width: 100%;
      border-collapse: collapse;
      margin-bottom: 20px;
    }

    .items-table th,
    .items-table td {
      border: 1px solid #ddd;
      padding: 8px;
      text-align: left;
      font-size: 10pt;
    }

    .items-table th {
      background-color: #f5f5f5;
      font-weight: bold;
    }

    .items-table .center {
      text-align: center;
    }

    .items-table .right {
      text-align: right;
    }

    .totals-table {
      margin-left: auto;
      border-collapse: collapse;
      margin-top: 10px;
    }

    .totals-table td {
      padding: 5px 15px;
      border: none;
      font-size: 11pt;
    }

    .totals-table .label {
      font-weight: bold;
      text-align: left;
    }

    .totals-table .amount {
      text-align: right;
      min-width: 80px;
    }

    .grand-total {
      font-weight: bold;
      border-top: 2px solid #333;
      margin-top: 5px;
    }

    .footer {
      margin-top: 30px;
      font-size: 10pt;
      color: #666;
      text-align: center;
    }
  </style>
</head>
<body>
  <div class="header">
    <h1>FACTUUR</h1>
    <div class="invoice-details">
      <div><strong>Factuurnummer:</strong> ${factuurnummer}</div>
      <div><strong>Factuurdatum:</strong> ${date}</div>
      <div><strong>Ordernummer:</strong> ${orderRef}</div>
    </div>
  </div>

  <div class="customer-info">
    <h3>Factuuradres</h3>
    <div class="customer-details">
      ${naam}<br>
      ${email !== 'Niet opgegeven' ? `${email}<br>` : ''}
      ${adres ? `${adres}<br>` : ''}
      ${postcodeCity ? `${postcodeCity}<br>` : ''}
      ${klant.countryCode ? `${klant.countryCode}` : ''}
    </div>
  </div>

  <table class="items-table">
    <thead>
      <tr>
        <th style="width: 15%;">Artikelcode</th>
        <th style="width: 45%;">Omschrijving</th>
        <th class="center" style="width: 8%;">Aantal</th>
        <th class="right" style="width: 12%;">Prijs ex.</th>
        <th class="center" style="width: 8%;">BTW</th>
        <th class="right" style="width: 12%;">Totaal</th>
      </tr>
    </thead>
    <tbody>
      ${rows}
    </tbody>
  </table>

  <table class="totals-table">
    <tr>
      <td class="label">Subtotaal (excl. BTW):</td>
      <td class="amount">${formatCurrency(exTotal)}</td>
    </tr>
    <tr>
      <td class="label">BTW (${num(DEFAULT_VAT * 100, 0)}%):</td>
      <td class="amount">${formatCurrency(vatTotal)}</td>
    </tr>
    <tr class="grand-total">
      <td class="label">TOTAAL (incl. BTW):</td>
      <td class="amount">${formatCurrency(grandTotal)}</td>
    </tr>
  </table>

  <div class="footer">
    <p>Betaling via Bol.com - Deze factuur is automatisch gegenereerd</p>
  </div>
</body>
</html>
`;

// Return ENKEL object voor "Run Once for Each Item" mode
return { 
  json: { 
    html,
    orderReference: orderRef,
    invoiceNumber: factuurnummer,
    grandTotal: grandTotal
  }
};

// Generate Review URL - voor "Run Once for Each Item" mode
const orderData = $json;

// Haal de BPID op uit de order data
const bolProductId = orderData.bolProductId || orderData.bpid || orderData.ean;
console.log('Using bolProductId:', bolProductId);

// Haal product info op
const orderItems = orderData.orderItems?.orderItems || orderData.orderItems || [];
const firstItem = orderItems[0] || {};
const productName = firstItem.product?.title || firstItem.title || 'product';

// Verbeterde slug generator
const slug = productName
  .toLowerCase()
  .replace(/[^a-z0-9\s-]/g, '') // Verwijder speciale tekens
  .replace(/\s+/g, '-')         // Spaties naar streepjes
  .replace(/-+/g, '-')          // Multiple streepjes naar √©√©n
  .replace(/^-+|-+$/g, '')      // Streepjes aan begin/eind weg
  .trim();


// Genereer review URL
const reviewUrl = `https://www.bol.com/nl/nl/p/${slug}/${bolProductId}/?mode=reviewStart`;

// Return ENKEL object voor "Run Once for Each Item" mode
return {
  json: {
    ...orderData,
    reviewUrl: reviewUrl,
    slug: slug,
    productName: productName
  }
};
